import React, { useState } from "react";

export default function UploadForm() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setError("");

    if (selectedFile) {
      // Validar tipo de arquivo
      const allowedTypes = ['application/pdf', 'text/xml', 'application/xml'];
      if (!allowedTypes.includes(selectedFile.type)) {
        setError("Por favor, selecione um arquivo PDF ou XML válido.");
        setFile(null);
        return;
      }

      // Validar tamanho (máximo 10MB)
      if (selectedFile.size > 10 * 1024 * 1024) {
        setError("O arquivo deve ter no máximo 10MB.");
        setFile(null);
        return;
      }

      setFile(selectedFile);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Por favor, selecione um arquivo primeiro.");
      return;
    }

    setLoading(true);
    setError("");
    setResult("");

    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Erro ao processar arquivo");
      }

      setResult(data.result || "Nenhum resultado encontrado.");
    } catch (err) {
      setError(err.message || "Erro ao enviar arquivo. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(result);
      // Feedback visual temporário
      const button = document.getElementById('copy-button');
      if (button) {
        const originalText = button.textContent;
        button.textContent = "Copiado!";
        button.className = "mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors";
        setTimeout(() => {
          button.textContent = originalText;
          button.className = "mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors";
        }, 2000);
      }
    } catch (err) {
      setError("Erro ao copiar para a área de transferência");
    }
  };

  const handleClear = () => {
    setFile(null);
    setResult("");
    setError("");
    // Limpar o input de arquivo
    const fileInput = document.getElementById('file-input');
    if (fileInput) fileInput.value = '';
  };

  return (
    <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg p-8">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">
          Decodificador de Nota Fiscal
        </h1>
        <p className="text-gray-600">
          Faça upload de um arquivo PDF ou XML de nota fiscal para extrair informações automaticamente
        </p>
      </div>

      {/* Área de Upload */}
      <div className="mb-6">
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-blue-400 transition-colors">
          <input
            id="file-input"
            type="file"
            accept=".pdf,.xml"
            onChange={handleFileChange}
            className="hidden"
          />
          <label htmlFor="file-input" className="cursor-pointer">
            <div className="text-gray-600">
              <svg className="mx-auto h-12 w-12 mb-4" stroke="currentColor" fill="none" viewBox="0 0 48 48">
                <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
              <p className="text-lg font-medium">
                {file ? file.name : "Clique para selecionar um arquivo"}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                PDF ou XML (máximo 10MB)
              </p>
            </div>
          </label>
        </div>

        {file && (
          <div className="mt-4 p-3 bg-blue-50 rounded-lg">
            <p className="text-sm text-blue-800">
              <strong>Arquivo selecionado:</strong> {file.name} ({(file.size / 1024 / 1024).toFixed(2)} MB)
            </p>
          </div>
        )}
      </div>

      {/* Botões */}
      <div className="flex gap-4 mb-6">
        <button
          onClick={handleUpload}
          disabled={!file || loading}
          className={`flex-1 px-6 py-3 rounded-lg font-medium transition-colors ${
            !file || loading
              ? "bg-gray-300 text-gray-500 cursor-not-allowed"
              : "bg-blue-500 text-white hover:bg-blue-600"
          }`}
        >
          {loading ? (
            <span className="flex items-center justify-center">
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Processando...
            </span>
          ) : (
            "Enviar para Análise"
          )}
        </button>

        {file && (
          <button
            onClick={handleClear}
            className="px-6 py-3 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
          >
            Limpar
          </button>
        )}
      </div>

      {/* Mensagens de Erro */}
      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
          <p className="text-red-800">{error}</p>
        </div>
      )}

      {/* Resultado */}
      {result && (
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-gray-800">Resultado da Análise</h3>
            <button
              id="copy-button"
              onClick={handleCopy}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
            >
              Copiar Resultado
            </button>
          </div>
          <div className="bg-gray-50 rounded-lg p-4">
            <textarea
              className="w-full h-96 p-4 border rounded-lg bg-white resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={result}
              readOnly
              placeholder="O resultado da análise aparecerá aqui..."
            />
          </div>
        </div>
      )}
    </div>
  );
}
